import classes from "./button.module.css";
// Textonly
// Dark
// Light
// Main
// Icon
function Button({ children, theme, size, textOnly, ...props }) {
  let btnClasses = `${classes.button_rounded}`;
  if (theme === "dark") {
    btnClasses += ` ${classes.button_dark}`;
  }
  if (theme === "light") {
    btnClasses += ` ${classes.button_light}`;
  }
  if (theme === "main") {
    btnClasses += ` ${classes.button_main}`;
  }

  if (size === "lg") {
    btnClasses += ` ${classes.button_lg}`;
  }

  if (size === "sm") {
    btnClasses += ` ${classes.button_sm}`;
  }

  return (
    <button className={btnClasses} {...props}>
      {children}
    </button>
  );
}

export default Button;
