import "./index.css";
import RootLayout from "./components/Root";
import Header from "./components/Header/Header";
import JobPage from "./components/Job/Job";
import SubHeaderPage from "./components/Header/SubHeader";
import ApplicantsPage from "./components/Pages/Applicants";
import MatchPage from "./components/Pages/Match";
import MessagesPage from "./components/Pages/Messages";

import { useState } from "react";
import { createBrowserRouter, RouterProvider } from "react-router-dom";

function App() {
  const [navIsOpen, setNavIsOpen] = useState(false);

  function handleClickNav() {
    setNavIsOpen((prevState) => !prevState);
  }

  const router = createBrowserRouter([
    {
      path: "/",
      element: <RootLayout onClick={handleClickNav} isOpen={navIsOpen} />,
      children: [
        { path: "/", element: <JobPage /> },
        { index: true, element: <JobPage /> },
      ],
    },
  ]);

  return <RouterProvider router={router} />;
  // return (
  //   <>
  //     <Header />
  //     <JobPage />
  //   </>
  // );
}

export default App;
