import classes from "./button.module.css";
// Textonly
// Dark
// Light
// Main
// Icon
// if (theme) {
//   btnTheme = `button_${theme}`;
//   btnClasses = `${btnSize} ${btnTheme}`;
// }
// if (textOnly) {
//   btnClasses = `${btnSize} ${classes.button_text}`;
// }
function Button({ children, theme, size, textOnly, ...props }) {
  let btnClasses = "";
  let btnSize, btnTheme;
  btnSize = `button_${size}`;

  if (theme) {
    btnTheme = `button_${theme}`;
  }

  btnClasses = textOnly
    ? `${btnSize} ${classes.button_text}`
    : `${btnSize} ${btnTheme}`;

  return (
    <button className={btnClasses} {...props}>
      {children}
    </button>
  );
}

export default Button;
