import classes from "./button.module.css";
import { NavLink } from "react-router-dom";

function Button({
  children,
  Icon,
  to = "",
  styles = "",
  theme,
  size,
  textOnly,
  ...props
}) {
  const btnSizeClass = `button_${size}`;
  const btnThemeClass = `button_${theme}`;

  const btnClasses = `${classes.button_rounded} ${classes[btnSizeClass]} ${classes[btnThemeClass]} ${styles}`;

  return (
    <button className={btnClasses} {...props}>
      {children}
    </button>
  );
  // return (
